/**
 * @usedBy ThfPageBlockedUserComponent
 *
 * @description
 *
 * Interface que define os valores de customização da mensagem de bloqueio do componente `thf-page-blocked-user`.
 */
export interface ThfPageBlockedUserReasonParams {
    /** Quantidade máxima de tentativas. */
    attempts?: number;
    /** Quantidade de dias para expiração de senha. */
    days?: number;
    /** Horas que o sistema permanecerá bloqueado. */
    hours?: number;
}
