/**
 * @usedBy ThfPageChangePasswordComponent
 *
 * @description
 *
 * Interface com a definição dos objetos aceitos pela propriedade `t-password-requirements`.
 */
export interface ThfPageChangePasswordRequirement {
    /** Requisito. */
    requirement: string;
    /**
     * Função que deve retornar um booleano para validar um requisito de senha.
     *
     * Também é possível informar diretamente um valor booleano que representa esta validação.
     */
    status: boolean | Function;
}
