import { ThfDynamicFormField, ThfPageDefault } from '@totvs/thf-ui';
/**
 * @docsPrivate
 *
 * @description
 *
 * Interface para o atributo `filter` do componente `thf-page-dynamic-search`.
 */
export interface ThfPageDynamicSearch extends ThfPageDefault {
    /**
     * @description
     *
     * Nome do método a ser executado quando a busca avançada for finalizada.
     */
    advancedSearch?: Function;
    /**
     * @description
     *
     * Lista dos filtros usados na busca avançada.
     */
    filters?: Array<ThfDynamicFormField>;
    /**
     * @description
     *
     * Nome do método a ser executado quando é pressionado a tecla `ENTER` dentro do `input` de pesquisa rápida do componente, o mesmo
     * receberá o valor digitado ao ser invocado.
     */
    quickSearch?: Function;
}
