import { ThfDynamicFormField } from '@totvs/thf-ui';
/**
 * @usedBy ThfPageDynamicTableComponent
 *
 * @description
 *
 * Interface dos fields usados para compor o template `thf-page-dynamic-table`.
 */
export interface ThfPageDynamicTableField extends ThfDynamicFormField {
    /** Indica se o campo será duplicado caso seja executada a ação de duplicação. */
    duplicate?: boolean;
    /** Indica se o campo será usado para busca avançada. */
    filter?: boolean;
    /** Tamanho da coluna em pixels ou porcetagem. */
    width?: number | string;
}
