import { ThfModalPasswordRecoveryType } from '../../thf-modal-password-recovery/enums/thf-modal-password-recovery-type.enum';
/**
 * @usedBy ThfPageLoginComponent
 *
 * @description
 *
 * Interface para especificação do tipo de recuperação de senha no `thf-modal-password-recovery`.
 */
export interface ThfPageLoginRecovery {
    /** Definição do e-mail que é exibido na mensagem para contato de suporte. */
    contactMail?: string;
    /** Definição da máscara do campo de telefone. */
    phoneMask?: string;
    /**
     * @optional
     *
     * @description
     *
     * Enum para especificação do tipo de recuperação de senha [ThfModalPasswordRecoveryType](/documentation/thf-modal-password-recovery).
     *
     * > Caso não seja definido valor se assume o padrão `ThfModalPasswordRecoveryType.Email`.
     */
    type?: ThfModalPasswordRecoveryType;
    /**
     * Endpoint usado pelo template **ThfModalPasswordRecovery** para requisição do recurso.
     *
     * > Saiba mais em [ThfModalPasswordRecovery](/documentation/thf-modal-password-recovery).
     */
    url: string;
}
