import { ThfBreadcrumbItem } from './thf-breadcrumb-item.interface';
/**
 * @usedBy
 *
 * ThfPageDefaultComponent, ThfPageDetailComponent, ThfPageEditComponent, ThfPageListComponent, ThfPageDynamicSearchBaseComponent
 *
 * @description
 *
 * Interface que define o `thf-breadcrumb`.
 */
export interface ThfBreadcrumb {
    /**
     * @optional
     *
     * @description
     *
     * Permite definir uma URL para favoritar ou desfavoritar.
     *
     * > Para maiores informações verificar a propriedade `t-favorite-service` do componente `thf-breadcrumb`.
     *
     */
    favorite?: string;
    /**
     * @description
     *
     * Lista de itens do _breadcrumb_.
     *
     * **Exemplo:**
     * ```
     * { label: 'Thf Portal', link: 'portal' }
     * ```
     */
    items: Array<ThfBreadcrumbItem>;
    /**
     * @optional
     *
     * @description
     *
     * Objeto que possibilita o envio de parâmetros adicionais à requisição.
     */
    params?: object;
}
