import { Observable } from 'rxjs';
import { ThfMenuItemFiltered } from '../thf-menu-item/thf-menu-item-filtered.interface';
/**
 * @usedBy ThfMenuComponent
 *
 * @description
 *
 * Interface do serviço utilizado no componente `thf-menu`.
 */
export interface ThfMenuFilter {
    /**
     * Método responsável por retornar um *Observable* que retorne uma lista de objetos que seguem a interface `ThfMenuItemFiltered`.
     * Será informado por parâmetro o valor a ser pesquisado e as informações adicionais preenchidas através da propriedade `t-params`.
     *
     * @param {string} search Valor informado no campo de busca dos itens de menus.
     * @param {any} params Valor informado através da propriedade `t-params`.
     */
    getFilteredData(search: string, params?: any): Observable<Array<ThfMenuItemFiltered>>;
}
