/**
 * @usedBy ThfTableComponent
 *
 * @description
 *
 * <a id="tableColumnIcon"></a>
 *
 * Interface que define a coluna com ícone(s) do `thf-table`.
 */
export interface ThfTableColumnIcon {
    /** Define a ação que será executada ao clicar no ícone. */
    action?: Function;
    /**
     * @optional
     *
     * @description
     *
     * Define a cor do ícone.
     *
     * Valores válidos:
     * - <span class="dot thf-color-01"></span> `color-01`
     * - <span class="dot thf-color-02"></span> `color-02`
     * - <span class="dot thf-color-03"></span> `color-03`
     * - <span class="dot thf-color-04"></span> `color-04`
     * - <span class="dot thf-color-05"></span> `color-05`
     * - <span class="dot thf-color-06"></span> `color-06`
     * - <span class="dot thf-color-07"></span> `color-07`
     * - <span class="dot thf-color-08"></span> `color-08`
     * - <span class="dot thf-color-09"></span> `color-09`
     * - <span class="dot thf-color-10"></span> `color-10`
     * - <span class="dot thf-color-11"></span> `color-11`
     * - <span class="dot thf-color-12"></span> `color-12`
     *
     */
    color?: string | Function;
    /** Função que deve retornar um booleano para habilitar ou desabilitar o ícone e sua ação. */
    disabled?: Function;
    /**
     * Ícone a ser exibido.
     *
     * > Veja os valores válidos na [Biblioteca de ícones](/guides/icons).
     */
    icon?: string;
    /** Define um texto de ajuda que será exibido ao passar o *mouse* em cima do ícone. */
    tooltip?: string;
    /**
     * Define o valor do respectivo ícone que será exibido, o qual se refere a propriedade `icon`.
     *
     * > Caso propriedade `icon` não for informada, o componente utilizará o próprio `value` como valor.
     */
    value: string;
}
