import { ThfDialogAlertLiterals } from './thf-dialog-alert-literals.interface';
import { ThfDialogConfirmLiterals } from './thf-dialog-confirm-literals.interface';
/**
 * @docsPrivate
 *
 * @usedBy ThfDialogService
 *
 * @description
 *
 * Interface para o título e a mensagem do serviço thf-dialog.
 */
interface ThfDialogOptions {
    /** Título da caixa de diálogo. */
    title: string;
    /** Mensagem da caixa de diálogo. */
    message: string;
}
/**
 * @usedBy ThfDialogService
 *
 * @docsExtends ThfDialogOptions
 *
 * @description
 *
 * Interface com as propriedades da caixa de diálogo de alerta do serviço thf-dialog.
 */
export interface ThfDialogAlertOptions extends ThfDialogOptions {
    /** Ação executada ao fechar o alerta pelo botão "Ok". */
    ok?: Function;
    /**
     * Objeto com as literais usadas no `thf-dialog` do tipo alerta.
     *
     * Para customizar o *label*, pode ser enviado o objeto da seguinte forma:
     *
     * ```typescript
     * this.thfDialog.alert({
     *   literals: { ok: 'Close' },
     *   title: 'Info message',
     *   message: 'Message body dialog'
     * });
     * ```
     *
     * > O objeto padrão de literais será traduzido de acordo com o idioma do *browser* (pt, en, es).
     */
    literals?: ThfDialogAlertLiterals;
}
/**
 * @usedBy ThfDialogService
 *
 * @docsExtends ThfDialogOptions
 *
 * @description
 *
 * Interface com as propriedades da caixa de diálogo de confirmação do serviço thf-dialog.
 */
export interface ThfDialogConfirmOptions extends ThfDialogOptions {
    /** Ação de confirmação da caixa de diálogo. */
    confirm: Function;
    /** Ação de cancelamento da caixa de diálogo. */
    cancel?: Function;
    /**
     * Objeto com as literais usadas no `thf-dialog` do tipo confirmação.
     *
     * Para customizar os *labels*, pode ser enviado o objeto da seguinte forma:
     *
     * ```typescript
     * this.thfDialog.confirm({
     *   literals: { cancel: 'No', confirm: 'Yes' },
     *   title: 'Confirm',
     *   message: 'Message body dialog',
     *   confirm: () => this.confirmOperation()
     * });
     * ```
     *
     * > O objeto padrão de literais será traduzido de acordo com o idioma do *browser* (pt, en, es).
     */
    literals?: ThfDialogConfirmLiterals;
}
export {};
